int n , k , d , ti[MAX_N] , g[MAX_N] , f[MAX_N][MAX_M] , sv[MAX_M] , C[MAX_N][MAX_N] ;

int cal(int n) {
	int m = 1e5 , bk = n / m , st = bk * m , res = num[bk] ;
	for (int i = st + 1 ; i <= n ; ++i) res = (long long)res * i % mod ;

	return res ;
}

int qpow(int a , int t) {
	int s = 1 ;
	for (; t ; t >>= 1 , a = (ll)a * a % mod)
		if (t & 1) s = (ll)s * a % mod ;

	return s ;
}

int main() {
	scanf("%d %d %d" , &n , &k , &d) ;

	C[0][0] = 1 ;
	for (int i = 1 ; i <= n ; ++i) {
		C[i][0] = 1 ;
		for (int j = 1 ; j <= n ; ++j)
			C[i][j] = (C[i - 1][j] + C[i - 1][j - 1]) % mod ;
	}

	///

	int jc = 1 ; g[0] = 1 ;
	for (int i = 1 ; i < k ; ++i) {
		jc = (ll)jc * i % mod ; g[i] = qpow(jc , mod - 2) ;
	}

	ti[0] = 0 ; f[0][0] = 1 ;
	for (int i = 1 ; i <= n ; ++i) {
		ti[i] = ti[i - 1] + k - 1 ;

		for (int p = 0 ; p < k ; ++p)
			for (int q = 0 ; q <= ti[i - 1] ; ++q) {
				int tmp = (ll)f[i - 1][q] * g[p] % mod ;
				(f[i][p + q] += tmp) %= mod ;
			}
	}

	///

	int ned = d + n * k ;
	sv[0] = qpow(cal(ned) , mod - 2) ;
	for (int i = 1 ; i <= ti[n] ; ++i) sv[i] = (ll)sv[i - 1] * (ned - i + 1) % mod ;

	int ans = 0 ;
	for (int i = 0 ; i <= n ; ++i) {
		int xs = C[n][i] , sum = 0 ;
		if (i & 1) xs = (mod - xs) % mod ;

		for (int j = 0 ; j <= ti[i] ; ++j) {
			int tmp = (ll)f[i][j] * qpow(n - i , ned - j) % mod ;
			tmp = (ll)tmp * sv[j] % mod ; (sum += tmp) %= mod ;
		}

		sum = (ll)sum * xs % mod ;
		(ans += sum) %= mod ;
	}

	ans = (ll)ans * cal(d) % mod ;
	printf("%d\n" , ans) ;

	return 0 ;
}